import Axios from "axios";

let userService = { endpoint: 'https://localhost:50001/api/hastyusers' }

userService.add = (payload) => {
  const config = {
    method: "POST",
    url: userService.endpoint,
    data: payload,
    withCredentials: true,
    crossdomain: true,
    headers: { "Content-Type": "application/json" },
  };
  return Axios(config).then((response) => response.data.item);
}

export default userService;
